package com.lilike.homework.stringalgorith;

import java.util.*;

/**
 *  找到字符串中的所有字母异位词
 *   https://leetcode-cn.com/problems/find-all-anagrams-in-a-string/
 * @Author llk
 * @Date 2020/9/28 16:44
 *
 * @Version 1.0
 */
public class FindAnagrams {

    public List<Integer> findAnagrams(String s, String p) {
        List<Integer> rest = new ArrayList<>();
        Set<Character> set = new HashSet<>();
        for (int i = 0; i < p.length(); i++) {
            set.add(p.charAt(i));
        }
        char[] temp = new char[p.length()];
        int tempI = 0;
        // 然后遍历
        for (int i = 0; i < s.length(); i++) {
            if (!set.contains(s.charAt(i))) continue;
            for (int j = i; j < i + p.length() && j  < s.length(); j++) {
                if (!set.contains(s.charAt(j))) {
                    break;
                }
                temp[tempI++] = s.charAt(j);
            }
            Arrays.sort(temp);
            if (p.equals(new String(temp))) {
                rest.add(i);
            }
            tempI = 0;
            Arrays.fill(temp,'.');
        }
        
        return rest;
    }
    
    
    
    
    
    public static void main(String[] args) {
        System.out.println(new FindAnagrams().findAnagrams("" , "yqrbgjdwtcaxzsnifvhmou"));
    }

}
